import React, { useEffect, useState } from "react";
import { useQuiz } from "../contexts/QuizContext"; // Access the context
import Header from "./Header";

const QuizList = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuizLocal, setSelectedQuizLocal] = useState("");
  const { setSelectedQuiz } = useQuiz(); // Get setSelectedQuiz from context

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const response = await fetch("/api/quiznames");
        const data = await response.json();
        setQuizzes(data); // Assuming data is an array of objects with `name` property
      } catch (error) {
        console.error("Error fetching quizzes:", error);
      }
    };

    fetchQuizzes();
  }, []);

  const handleSelectionChange = (event) => {
    setSelectedQuizLocal(event.target.value);
  };

  const handleSubmit = () => {
    if (selectedQuizLocal) {
      setSelectedQuiz(selectedQuizLocal); // Update quiz name in context
      // Navigate to quiz page or home if needed
    }
  };

  return (
    <div className="app">
      <Header />
      <h2>Select a Quiz</h2>
      <ul>
        {quizzes.map((quiz) => (
          <li key={quiz.name}>
            <label>
              <input
                type="radio"
                value={quiz.name}
                checked={selectedQuizLocal === quiz.name}
                onChange={handleSelectionChange}
              />
              {quiz.name}
            </label>
          </li>
        ))}
      </ul>
      <button
        className="app"
        onClick={handleSubmit}
        disabled={!selectedQuizLocal}
      >
        Submit
      </button>
    </div>
  );
};

export default QuizList;
