import { useNavigate } from "react-router-dom";
import Header from "./Header";
import React, { useEffect, useState } from "react";
import { useQuiz } from "./QuizContext"; // Access the context

const QuizList = ({ onSelectQuiz }) => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState("");
  const navigate = useNavigate(); // React Router's navigation hook

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/quiznames/");
        const data = await response.json();
        setQuizzes(data); // Assuming data is an array of objects
      } catch (error) {
        console.error("Error fetching quizzes:", error);
      }
    };

    fetchQuizzes();
  }, []);

  const handleSelectionChange = (event) => {
    setSelectedQuiz(event.target.value);
  };

  const handleSubmit = () => {
    onSelectQuiz(selectedQuiz); // Pass the selected quiz back to the parent
    navigate("/");
  };

  return (
    <div className="app">
      <Header />
      <h2>Select a Quiz to Start</h2>
      <ul>
        {quizzes.map((quiz) => (
          <li key={quiz.name}>
            {" "}
            {/* Use quiz.name instead of quiz */}
            <label>
              <input
                type="radio" // Change to type="checkbox" if you want checkboxes
                value={quiz.name} // Set value to quiz.name
                checked={selectedQuiz === quiz.name} // Check if selected
                onChange={handleSelectionChange}
              />
              {quiz.name} {/* Render quiz name */}
            </label>
          </li>
        ))}
      </ul>
      <span>&ensp;</span>
      <button
        className="btn btn-ui"
        onClick={handleSubmit}
        disabled={!selectedQuiz}
      >
        Submit
      </button>
    </div>
  );
};

export default QuizList;
