import { useQuiz } from "../contexts/QuizContext";

function StartScreen() {
  const { numQuestions, dispatch, selectedQuiz } = useQuiz();
  return (
    <div className="start">
      <h2>
        You picked the quiz <span style={{ color: "red" }}>{selectedQuiz}</span>{" "}
        !
      </h2>

      <h3>
        {numQuestions} questions in quiz {selectedQuiz}
      </h3>
      <button
        className="btn btn-ui spaced"
        onClick={() => dispatch({ type: "start" })}
      >
        Let's start
      </button>
    </div>
  );
}

export default StartScreen;
